import { renderHook, act } from '@testing-library/react';
import { useServiceWorker } from '../../hooks/useServiceWorker';

// Mock navigator.serviceWorker
const mockServiceWorker = {
  register: jest.fn(),
  ready: jest.fn(),
  addEventListener: jest.fn(),
  controller: null,
};

Object.defineProperty(navigator, 'serviceWorker', {
  value: mockServiceWorker,
  writable: true,
});

describe('useServiceWorker', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    // Reset service worker support
    Object.defineProperty(navigator, 'serviceWorker', {
      value: mockServiceWorker,
      writable: true,
    });
  });

  afterEach(() => {
    jest.clearAllTimers();
  });

  it('should initialize with default values', () => {
    const { result } = renderHook(() => useServiceWorker());

    expect(result.current.isSupported).toBe(true);
    expect(result.current.isRegistered).toBe(false);
    expect(result.current.isUpdating).toBe(false);
    expect(result.current.updateAvailable).toBe(false);
  });

  it('should register service worker successfully', async () => {
    const mockRegistration = {
      addEventListener: jest.fn(),
    };

    mockServiceWorker.register.mockResolvedValue(mockRegistration);

    const { result } = renderHook(() => useServiceWorker());

    await act(async () => {
      // Wait for useEffect to run
      await new Promise(resolve => setTimeout(resolve, 0));
    });

    expect(mockServiceWorker.register).toHaveBeenCalledWith('/sw.js');
    expect(result.current.isRegistered).toBe(true);
  });

  it('should handle service worker registration failure', async () => {
    const consoleErrorSpy = jest.spyOn(console, 'error').mockImplementation();

    mockServiceWorker.register.mockRejectedValue(new Error('Registration failed'));

    const { result } = renderHook(() => useServiceWorker());

    await act(async () => {
      await new Promise(resolve => setTimeout(resolve, 0));
    });

    expect(consoleErrorSpy).toHaveBeenCalledWith(
      '[SW] Service worker registration failed:',
      expect.any(Error)
    );

    consoleErrorSpy.mockRestore();
  });

  it('should handle update found event', async () => {
    const mockRegistration = {
      addEventListener: jest.fn(),
      installing: {
        addEventListener: jest.fn(),
        state: 'installing',
      },
    };

    mockServiceWorker.register.mockResolvedValue(mockRegistration);

    const { result } = renderHook(() => useServiceWorker());

    await act(async () => {
      await new Promise(resolve => setTimeout(resolve, 0));
    });

    // Trigger updatefound event
    const updateFoundCallback = mockRegistration.addEventListener.mock.calls.find(
      call => call[0] === 'updatefound'
    )[1];

    act(() => {
      updateFoundCallback();
    });

    expect(result.current.isUpdating).toBe(true);

    // Simulate worker state change to installed
    const stateChangeCallback = mockRegistration.installing.addEventListener.mock.calls.find(
      call => call[0] === 'statechange'
    )[1];

    act(() => {
      stateChangeCallback();
    });

    expect(result.current.updateAvailable).toBe(true);
    expect(result.current.isUpdating).toBe(false);
  });

  it('should handle controller change', async () => {
    const mockRegistration = {
      addEventListener: jest.fn(),
    };

    mockServiceWorker.register.mockResolvedValue(mockRegistration);
    const mockReload = jest.fn();
    Object.defineProperty(window, 'location', {
      value: { reload: mockReload },
      writable: true,
    });

    renderHook(() => useServiceWorker());

    await act(async () => {
      await new Promise(resolve => setTimeout(resolve, 0));
    });

    // Trigger controllerchange event
    const controllerChangeCallback = mockServiceWorker.addEventListener.mock.calls.find(
      call => call[0] === 'controllerchange'
    )[1];

    act(() => {
      controllerChangeCallback();
    });

    expect(mockReload).toHaveBeenCalled();
  });

  it('should handle message events', async () => {
    const mockRegistration = {
      addEventListener: jest.fn(),
    };

    mockServiceWorker.register.mockResolvedValue(mockRegistration);
    const consoleLogSpy = jest.spyOn(console, 'log').mockImplementation();

    renderHook(() => useServiceWorker());

    await act(async () => {
      await new Promise(resolve => setTimeout(resolve, 0));
    });

    // Trigger message event
    const messageCallback = mockServiceWorker.addEventListener.mock.calls.find(
      call => call[0] === 'message'
    )[1];

    const mockEvent = { data: 'test message' };

    act(() => {
      messageCallback(mockEvent);
    });

    expect(consoleLogSpy).toHaveBeenCalledWith('[SW] Message from service worker:', 'test message');

    consoleLogSpy.mockRestore();
  });

  it('should update service worker', async () => {
    const mockRegistration = {
      update: jest.fn().mockResolvedValue(undefined),
    };

    mockServiceWorker.ready.mockResolvedValue(mockRegistration);

    const { result } = renderHook(() => useServiceWorker());

    await act(async () => {
      await result.current.updateServiceWorker();
    });

    expect(mockServiceWorker.ready).toHaveBeenCalled();
    expect(mockRegistration.update).toHaveBeenCalled();
  });

  it('should skip waiting when update is available', async () => {
    const mockRegistration = {
      waiting: {
        postMessage: jest.fn(),
      },
    };

    mockServiceWorker.ready.mockResolvedValue(mockRegistration);

    const { result } = renderHook(() => useServiceWorker());

    // Manually set update available
    act(() => {
      // Simulate update available state
      result.current.updateAvailable = true;
    });

    await act(async () => {
      await result.current.skipWaiting();
    });

    expect(mockServiceWorker.ready).toHaveBeenCalled();
    expect(mockRegistration.waiting.postMessage).toHaveBeenCalledWith({ type: 'SKIP_WAITING' });
  });

  it('should not skip waiting when no update is available', async () => {
    const { result } = renderHook(() => useServiceWorker());

    await act(async () => {
      await result.current.skipWaiting();
    });

    expect(mockServiceWorker.ready).not.toHaveBeenCalled();
  });

  it('should handle unsupported service worker', () => {
    // Remove service worker support
    delete (navigator as any).serviceWorker;

    const { result } = renderHook(() => useServiceWorker());

    expect(result.current.isSupported).toBe(false);
    expect(result.current.isRegistered).toBe(false);
  });
});

import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Sun, Moon, Globe, LayoutDashboard, Activity, Brain, Lightbulb, AlertCircle, Settings } from 'lucide-react';
import Dashboard from '@/pages/Dashboard';

type PlatformStatus = {
  statusGeneral: string;
  componentes: {
    apiPrincipal: { status: string; latencia_ms: number };
    baseDeDatos: { status: string; conexionesActivas: number };
    motorPredictivoIA: { status: string; modelosCargados: number };
    pipelineDeDatos: { status: string; ultimoIngreso: string };
  };
  analisisActivos: number;
  alertasCriticas: number;
  cargaDelSistema: number;
};

const StatusDot: React.FC<{ status: string }> = ({ status }) => {
  let color = 'bg-gray-400';
  if (status === 'ONLINE' || status === 'ACTIVO') color = 'bg-green-500';
  else if (status === 'DEGRADADO') color = 'bg-yellow-400';
  else if (status === 'OFFLINE' || status === 'INACTIVO') color = 'bg-red-500';
  return <span className={`inline-block w-2 h-2 rounded-full mr-2 ${color}`}></span>;
};

const NavItem: React.FC<{ to?: string; icon: React.ReactNode; label: string; collapsed: boolean; status?: string }> = ({ to = '#', icon, label, collapsed, status }) => (
  <Link to={to} className="flex items-center space-x-3 px-3 py-2 rounded hover:bg-gray-700/40">
    <span className="w-5 h-5">{icon}</span>
    {!collapsed && <span className="flex items-center text-sm">{status && <StatusDot status={status} />}{label}</span>}
  </Link>
);

const CommandCenterLayout: React.FC = () => {
  const [collapsed, setCollapsed] = useState(false);
  const [dark, setDark] = useState(document.documentElement.classList.contains('dark'));
  const [platformStatus, setPlatformStatus] = useState<PlatformStatus | null>(null);
  const [loadingStatus, setLoadingStatus] = useState(true);
  const [isMockMode, setIsMockMode] = useState(() => {
    try {
      return typeof window !== 'undefined' && window.localStorage && window.localStorage.getItem('NATIVE_DEV_MODE') === 'true';
    } catch (e) {
      return false;
    }
  });

  useEffect(() => {
    if (dark) document.documentElement.classList.add('dark');
    else document.documentElement.classList.remove('dark');
  }, [dark]);

  useEffect(() => {
    console.log('CommandCenterLayout useEffect');
    let timer: NodeJS.Timeout;
    const fetchStatus = async () => {
      try {
        const token = (typeof window !== 'undefined' && window.localStorage.getItem('praevisio_token')) || 'demo-token';
        console.log('token:', token);
        // Safe runtime env accessor to avoid import.meta usage (Jest/Node incompatibilities)
        const getEnvFlag = (key: string) => {
          try {
            // Prefer a runtime-injected global
            if (typeof (globalThis as any).__RUNTIME_ENV__ !== 'undefined' && (globalThis as any).__RUNTIME_ENV__[key]) {
              return String((globalThis as any).__RUNTIME_ENV__[key]);
            }
            // Fallback to process.env when available (tests)
            if (typeof process !== 'undefined' && process.env && process.env[key]) {
              return String(process.env[key]);
            }
          } catch (e) {
            // ignore
          }
          return undefined;
        };
        const isTestMode = getEnvFlag('TEST_MODE') === 'true';
        const url = isTestMode ? 'http://localhost:3001/api/platform-status' : '/api/platform-status';
        console.log('fetching:', url, 'testMode:', isTestMode);
        const res = await fetch(url, { headers: { Authorization: `Bearer ${token}` } });
        console.log('res.ok:', res.ok, 'status:', res.status);
        if (!res.ok) throw new Error('fetch_error');
        const json = await res.json();
        console.log('json:', json);
        setPlatformStatus(json);
      } catch (err) {
        console.log('fetch error:', err);
        setPlatformStatus(null);
      } finally {
        setLoadingStatus(false);
      }
    };
    fetchStatus();
    timer = setInterval(fetchStatus, 60000);
    return () => clearInterval(timer);
  }, []);

  const getEnvFlagTop = (key: string) => {
    try {
      if (typeof (globalThis as any).__RUNTIME_ENV__ !== 'undefined' && (globalThis as any).__RUNTIME_ENV__[key]) {
        return String((globalThis as any).__RUNTIME_ENV__[key]);
      }
      if (typeof process !== 'undefined' && process.env && process.env[key]) {
        return String(process.env[key]);
      }
    } catch (e) {
      // ignore
    }
    return undefined;
  };

  const globalMockActive = (getEnvFlagTop('FORCE_MOCKS') === 'true') || isMockMode;

  return (
    <div className="min-h-screen flex bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
      {/* Global mock/banner indicator */}
      {globalMockActive && (
        <div className="fixed top-2 left-1/2 transform -translate-x-1/2 z-50">
          <div className="px-3 py-1 rounded-full bg-yellow-600 text-black text-sm font-semibold">MODO SIMULADO</div>
        </div>
      )}
      <aside className={`flex flex-col ${collapsed ? 'w-16' : 'w-64'} bg-etherblue-dark/80 text-white border-r border-gray-800 transition-all`} data-testid="sidebar-nav">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-white/10 rounded">
              <Globe className="w-5 h-5" />
            </div>
            {!collapsed && <div className="font-bold">Praevisio AI</div>}
          </div>
          <button aria-label="collapse" onClick={() => setCollapsed(s => !s)} className="p-2 rounded hover:bg-white/5" data-testid="sidebar-collapse-btn">
            {/* simple menu icon */}
            <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M4 6h16M4 12h16M4 18h16"/></svg>
          </button>
        </div>

        {/* Estado General */}
        {!collapsed && (
          <div className="px-4 pb-2" data-testid="platform-status">
            <div className="flex items-center space-x-2">
              <span className="font-bold text-lg">{platformStatus ? platformStatus.statusGeneral : 'Cargando...'}</span>
              {platformStatus && <StatusDot status={platformStatus.statusGeneral === 'OPERACIONAL' ? 'ONLINE' : 'OFFLINE'} />}
            </div>
          </div>
        )}

        <nav className="flex-1 p-2 space-y-1">
          <NavItem to="/dashboard" icon={<LayoutDashboard />} label="Visi√≥n General" collapsed={collapsed} status={platformStatus?.componentes?.apiPrincipal?.status} data-testid="nav-vision-general" />
          <NavItem to="#" icon={<Activity />} label="An√°lisis de Se√±ales" collapsed={collapsed} status={platformStatus?.componentes?.pipelineDeDatos?.status} data-testid="nav-analisis-de-senales" />
          <NavItem to="#" icon={<Brain />} label="Modelos Predictivos" collapsed={collapsed} status={platformStatus?.componentes?.motorPredictivoIA?.status} data-testid="nav-modelos-predictivos" />
          <NavItem to="#" icon={<Lightbulb />} label="Generador de Escenarios" collapsed={collapsed} data-testid="nav-generador-de-escenarios" />
          <NavItem to="#" icon={<AlertCircle />} label="Reportes y Alertas" collapsed={collapsed} data-testid="nav-reportes-y-alertas" />
          <NavItem to="#" icon={<Settings />} label="Configuraci√≥n" collapsed={collapsed} data-testid="nav-configuracion" />
        </nav>

        <div className="p-4" data-testid="user-profile">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gray-800 rounded-full flex items-center justify-center">A</div>
              {!collapsed && <div className="text-sm">Nombre de Analista<br/><span className="text-xs text-gray-300">Estratega de Riesgos</span></div>}
            </div>
            {!collapsed && (
              <div className="flex items-center space-x-2">
                <button onClick={() => setDark(d => !d)} className="p-2 rounded hover:bg-white/5" data-testid="theme-toggle-btn">
                  {dark ? <Sun className="w-4 h-4" /> : <Moon className="w-4 h-4" />}
                </button>

                {/* Toggle for mock mode (stores flag in localStorage) */}
                <button
                  onClick={() => {
                    try {
                      const next = !isMockMode;
                      setIsMockMode(next);
                      if (typeof window !== 'undefined' && window.localStorage) {
                        window.localStorage.setItem('NATIVE_DEV_MODE', next ? 'true' : 'false');
                      }
                    } catch (e) {
                      // ignore
                    }
                  }}
                  className={`px-2 py-1 rounded text-xs ${isMockMode ? 'bg-yellow-600 text-black' : 'bg-gray-700 text-white'}`}
                  data-testid="mock-toggle-btn"
                >
                  {isMockMode ? 'SIMULADO ON' : 'SIMULADO OFF'}
                </button>
              </div>
            )}
          </div>
        </div>
      </aside>

      <main className="flex-1 overflow-auto">
        <Dashboard />
      </main>
    </div>
  );
};

export default CommandCenterLayout;

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { ComposableMap, Geographies, Geography } from 'react-simple-maps';

const geoUrl = "https://cdn.jsdelivr.net/npm/world-atlas@2/countries-110m.json";

interface ResilienceData {
  country: string;
  socialEvents: number;
  resilienceScore: number;
  recommendations: string[];
  period: { startDate: string; endDate: string };
}

interface CommunityResilienceResponse {
  resilienceAnalysis: { [key: string]: ResilienceData };
  globalResilienceAssessment: any;
  timestamp: string;
}

interface CommunityResilienceWidgetProps {
  resilienceData?: CommunityResilienceResponse;
}

const CommunityResilienceWidget: React.FC<CommunityResilienceWidgetProps> = ({ resilienceData: resilienceData }) => {
  const [hoveredCountry, setHoveredCountry] = useState<string | null>(null);

  // If no resilienceData is provided, we show an informative state ‚Äî DemoSection is expected to supply data
  if (!resilienceData || !resilienceData.resilienceAnalysis) {
    return (
      <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
        <CardContent className="pt-6">
          <div className="text-center py-8">
            <p className="text-gray-300">Datos de resiliencia comunitaria no disponibles (orquestador)</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const getCountryColor = (countryCode: string) => {
    if (!resilienceData) return '#DDD';
    const countryData = resilienceData.resilienceAnalysis[countryCode];
    if (!countryData) return '#DDD';

    const score = countryData.resilienceScore;
    if (score >= 80) return '#10B981'; // Green
    if (score >= 60) return '#F59E0B'; // Yellow
    return '#EF4444'; // Red
  };

  const getTooltipContent = (countryCode: string) => {
    if (!resilienceData) return '';
    const countryData = resilienceData.resilienceAnalysis[countryCode];
    if (!countryData) return `${countryCode}: Sin datos`;

    return `
      <div style="max-width: 200px;">
        <strong>${countryData.country}</strong><br/>
        Resiliencia: ${countryData.resilienceScore.toFixed(1)}/100<br/>
        Eventos Sociales: ${countryData.socialEvents}<br/>
        <br/>
        <strong>Recomendaciones:</strong><br/>
        ${countryData.recommendations.slice(0, 2).join('<br/>')}
      </div>
    `;
  };


  return (
    <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
      <CardHeader>
        <CardTitle className="text-white">Resiliencia Comunitaria LATAM</CardTitle>
        <p className="text-gray-400 text-sm">Mapa interactivo de fortaleza social</p>
      </CardHeader>
      <CardContent>
        <div className="h-64" data-testid="resilience-map">
          <ComposableMap
            projection="geoMercator"
            projectionConfig={{
              scale: 300,
              center: [-60, -15]
            }}
            className="w-full h-full"
          >
            <Geographies geography={geoUrl}>
              {({ geographies }) =>
                geographies.map((geo) => {
                  const countryCode = geo.properties.ISO_A3;
                  return (
                    <Geography
                      key={geo.rsmKey}
                      geography={geo}
                      fill={getCountryColor(countryCode)}
                      stroke="#FFFFFF"
                      strokeWidth={0.5}
                      style={{
                        default: { outline: 'none' },
                        hover: { outline: 'none', fill: '#60A5FA' },
                        pressed: { outline: 'none' },
                      }}
                      onMouseEnter={() => setHoveredCountry(countryCode)}
                      onMouseLeave={() => setHoveredCountry(null)}
                    />
                  );
                })
              }
            </Geographies>
          </ComposableMap>
        </div>
        <div className="mt-4 flex justify-between text-sm text-gray-400">
          <span>üü¢ Alta Resiliencia (80-100)</span>
          <span>üü° Media Resiliencia (60-79)</span>
          <span>üî¥ Baja Resiliencia (0-59)</span>
        </div>
        {hoveredCountry && (
          <div className="mt-4 p-3 bg-gray-700/50 rounded-lg">
            {(() => {
              const countryData = resilienceData?.resilienceAnalysis[hoveredCountry];
              if (!countryData) return <p className="text-gray-300">Sin datos para {hoveredCountry}</p>;
              return (
                <div>
                  <h4 className="text-white font-semibold">{countryData.country}</h4>
                  <p className="text-gray-300">Resiliencia: {countryData.resilienceScore.toFixed(1)}/100</p>
                  <p className="text-gray-300">Eventos Sociales: {countryData.socialEvents}</p>
                  <div className="mt-2">
                    <p className="text-gray-400 text-sm">Recomendaciones:</p>
                    <ul className="text-xs text-gray-400 list-disc list-inside">
                      {countryData.recommendations.slice(0, 2).map((rec, idx) => (
                        <li key={idx}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              );
            })()}
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default CommunityResilienceWidget;

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';

interface EthicalVector {
  vector: [number, number, number]; // [humanImpact, environmentalSustainability, socialEquity]
  overallScore: number;
  assessment: string;
  timestamp: string;
}

interface EthicalAssessmentResponse {
  success: boolean;
  data: EthicalVector;
  isMock?: boolean;
}

interface Props {
  ethicalAssessment?: EthicalAssessmentResponse | null;
}

const EthicalVectorDisplay: React.FC<Props> = ({ ethicalAssessment = null }) => {
  const [error] = useState<string | null>(null);

  const ethicalData = ethicalAssessment && ethicalAssessment.data ? ethicalAssessment.data : null;
  const getScoreColor = (score: number) => {
    if (score >= 70) return 'text-green-400';
    if (score >= 40) return 'text-yellow-400';
    return 'text-red-400';
  };

  const getProgressColor = (score: number) => {
    if (score >= 70) return 'bg-green-500';
    if (score >= 40) return 'bg-yellow-500';
    return 'bg-red-500';
  };

  const getAssessmentColor = (assessment: string) => {
    if (assessment.includes('Low')) return 'text-green-400';
    if (assessment.includes('Medium')) return 'text-yellow-400';
    return 'text-red-400';
  };

  if (error || !ethicalData) {
    return (
      <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
        <CardContent className="pt-6">
          <div className="text-center py-8">
            <p className="text-red-400">{error || 'No hay datos √©ticos disponibles (orquestador no proporcion√≥ datos)'}</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const [humanImpact, environmentalSustainability, socialEquity] = ethicalData.vector;

  return (
    <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
      <CardHeader>
        <CardTitle className="text-white text-2xl">Vector √âtico - IA Explicable</CardTitle>
        <p className="text-gray-400 text-sm">
          Evaluaci√≥n √©tica de la √∫ltima predicci√≥n importante
        </p>
        <p className="text-xs text-gray-500 mt-1">
          √öltima actualizaci√≥n: {new Date(ethicalData.timestamp).toLocaleString()}
        </p>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Overall Assessment */}
        <div className="text-center p-4 bg-gray-700/50 rounded-lg">
          <div className={`text-4xl font-bold ${getAssessmentColor(ethicalData.assessment)}`}>
            {ethicalData.overallScore.toFixed(1)}%
          </div>
          <div className={`text-lg font-semibold ${getAssessmentColor(ethicalData.assessment)}`}>
            {ethicalData.assessment}
          </div>
          <p className="text-gray-400 text-sm mt-1">Puntuaci√≥n √âtica General</p>
        </div>

        {/* Ethical Vector Components */}
        <div className="space-y-4">
          <div>
            <div className="flex justify-between items-center mb-2">
              <span className="text-white font-medium">Impacto Humano</span>
              <span className={`font-bold ${getScoreColor(humanImpact)}`}>
                {humanImpact.toFixed(1)}%
              </span>
            </div>
            <Progress value={humanImpact} className="h-3" />
            <p className="text-xs text-gray-400 mt-1">
              Evaluaci√≥n del impacto potencial en poblaciones humanas
            </p>
          </div>

          <div>
            <div className="flex justify-between items-center mb-2">
              <span className="text-white font-medium">Sostenibilidad Ambiental</span>
              <span className={`font-bold ${getScoreColor(environmentalSustainability)}`}>
                {environmentalSustainability.toFixed(1)}%
              </span>
            </div>
            <Progress value={environmentalSustainability} className="h-3" />
            <p className="text-xs text-gray-400 mt-1">
              Medida de sostenibilidad y respeto al medio ambiente
            </p>
          </div>

          <div>
            <div className="flex justify-between items-center mb-2">
              <span className="text-white font-medium">Equidad Social</span>
              <span className={`font-bold ${getScoreColor(socialEquity)}`}>
                {socialEquity.toFixed(1)}%
              </span>
            </div>
            <Progress value={socialEquity} className="h-3" />
            <p className="text-xs text-gray-400 mt-1">
              Evaluaci√≥n de equidad en la distribuci√≥n de riesgos y beneficios
            </p>
          </div>
        </div>

