interface FoodSecurityData {
  country: string;
  year: number;
  prevalenceUndernourishment: number;
  riskIndex: number;
  volatilityIndex: number;
}

interface FoodSecurityResponse {
  data: FoodSecurityData[];
  timestamp?: string;
  source?: string;
  isMock?: boolean;
}

interface Props {
  foodSecurityData?: FoodSecurityResponse | null;
}

const FoodSecurityDashboard: React.FC<Props> = ({ foodSecurityData = null }) => {
  const [selectedCountry, setSelectedCountry] = useState<string>('COL');

  // Get unique countries
  const countries = Array.isArray(foodSecurityData?.data) ? [...new Set(foodSecurityData!.data.map(d => d.country))] : [];

  // Filter data for selected country
  const countryData = Array.isArray(foodSecurityData?.data) ? foodSecurityData!.data.filter(d => d.country === selectedCountry) : [];

  // Prepare chart data
  const chartData = Array.isArray(countryData) ? countryData.map(d => ({
    year: d.year,
    prevalence: d.prevalenceUndernourishment,
    riskIndex: d.riskIndex,
    volatility: d.volatilityIndex,
  })) : [];

  // Get latest data for selected country
  const latestData = Array.isArray(countryData) && countryData.length > 0 ? countryData.sort((a, b) => b.year - a.year)[0] : null;

  const getRiskColor = (risk: number) => {
    if (risk >= 70) return 'text-red-400';
    if (risk >= 40) return 'text-yellow-400';
    return 'text-green-400';
  };

  const getRiskLevel = (risk: number) => {
    if (risk >= 70) return 'Alto';
    if (risk >= 40) return 'Medio';
    return 'Bajo';
  };

  // If no data provided, render an informative empty state (frontend expects backend to provide structured data)
  if (!foodSecurityData || !Array.isArray(foodSecurityData.data)) {
    return (
      <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
        <CardContent className="pt-6">
          <div className="text-center py-8">
            <p className="text-gray-300">Datos de seguridad alimentaria no disponibles (esperando orquestador)</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card className="bg-gradient-to-r from-orange-900 to-red-900 border-gray-700">
        <CardHeader>
          <div className="flex justify-between items-center">
            <div>
              <CardTitle className="text-white text-2xl">Dashboard de Seguridad Alimentaria</CardTitle>
              <p className="text-gray-300 mt-1">
                √çndice de Riesgo de Hambruna y Volatilidad de Precios
              </p>
            </div>
            <Select value={selectedCountry} onValueChange={setSelectedCountry}>
              <SelectTrigger className="w-40 bg-gray-700 border-gray-600 text-white">
                <SelectValue />
              </SelectTrigger>
              <SelectContent className="bg-gray-700 border-gray-600">
                {Array.isArray(countries) && countries.map(country => (
                  <SelectItem key={country} value={country} className="text-white">
                    {country}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardHeader>
      </Card>

      {/* Current Status */}
      {latestData && (
        <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
          <CardContent className="pt-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="text-center">
                <div className={`text-3xl font-bold ${getRiskColor(latestData.riskIndex)}`}>
                  {latestData.riskIndex.toFixed(1)}%
                </div>
                <div className="text-sm text-gray-400">√çndice de Riesgo de Hambruna</div>
                <div className={`text-sm font-semibold ${getRiskColor(latestData.riskIndex)}`}>
                  Nivel: {getRiskLevel(latestData.riskIndex)}
                </div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-blue-400">
                  {latestData.prevalenceUndernourishment.toFixed(1)}%
                </div>
                <div className="text-sm text-gray-400">Prevalencia de Desnutrici√≥n</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-purple-400">
                  {latestData.volatilityIndex.toFixed(1)}%
                </div>
                <div className="text-sm text-gray-400">√çndice de Volatilidad</div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
          <CardHeader>
            <CardTitle className="text-white">Evoluci√≥n del Riesgo de Hambruna</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={chartData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis dataKey="year" stroke="#9CA3AF" />
                <YAxis stroke="#9CA3AF" />
