#!/usr/bin/env node
import fs from 'fs';
import { spawnSync } from 'child_process';
import path from 'path';

const workspaceRoot = path.resolve(new URL(import.meta.url).pathname, '..', '..');
const projectKanban = path.join(workspaceRoot, 'PROJECT_KANBAN.md');
const reportScript = path.join(workspaceRoot, 'scripts', 'generate_report.js');

function readKanban() {
  try {
    const content = fs.readFileSync(projectKanban, 'utf8');
    // Simple: pick the first line that looks like a task (starting with '- ')
    const lines = content.split(/\r?\n/);
    const task = lines.find(l => l.trim().startsWith('- '));
    return task ? task.trim().substring(2) : null;
  } catch (err) {
    return null;
  }
}

function runTests() {
  console.log('[Aion] Ejecutando test suite: npm test');
  const res = spawnSync('npm', ['test'], { stdio: 'inherit' });
  return res.status === 0;
}

function generateReport(success, details) {
  const res = spawnSync('node', [reportScript, success ? 'success' : 'failure', JSON.stringify(details || {})], { encoding: 'utf8' });
  if (res.error) console.error('[Aion] Error generando reporte:', res.error);
  else console.log(res.stdout);
}

async function main() {
  console.log('[Aion] Iniciando ciclo de introspección...');
  const mission = readKanban();
  if (!mission) {
    console.log('[Aion] No se encontró misión en PROJECT_KANBAN.md. Generando misión trivial.');
  } else {
    console.log('[Aion] Próxima misión detectada:', mission);
  }

  console.log('[Aion] Ejecutando misión (simulada)...');
  // Aquí normalmente se ejecutaría la misión real. Para seguridad, lo simulamos.
  await new Promise(r => setTimeout(r, 800));

  console.log('[Aion] Validando cambios con la suite de pruebas...');
  const ok = runTests();

  const details = {
    mission: mission || 'auto-generated: tidy-docs',
    timestamp: new Date().toISOString(),
    branch: process.env.GIT_BRANCH || null,
  };

  generateReport(ok, details);

  if (!ok) {
    console.log('[Aion] Tests fallaron. Deteniendo ciclo y reportando. No se hará commit en main.');
    process.exit(1);
  }

  console.log('[Aion] Ciclo completado con éxito. Reporte generado.');
}

main().catch(err => {
  console.error('[Aion] Error en ciclo:', err);
  process.exit(1);
});

#!/usr/bin/env node
// Asclepio: Attempt simple auto-heals for failing Playwright tests (demo proof-of-concept)
import fs from 'fs';
import path from 'path';

const [testPath, errorMessage] = process.argv.slice(2);
if (!testPath) {
  console.error('Usage: asclepio-healer.js <testPath> [errorMessage]');
  process.exit(1);
}

console.log(`[Asclepio] Attempting to heal test: ${testPath}`);
// Demo strategy: replace common fragile selectors like text= with data-testid hints if present in same folder
try {
  const abs = path.resolve(testPath);
  if (!fs.existsSync(abs)) {
    console.error('[Asclepio] test file does not exist, cannot heal');
    process.exit(2);
  }
  let content = fs.readFileSync(abs, 'utf-8');
  // Simple heuristic: replace "text=Planes y Precios" with "getByTestId('pricing-table')" style locator
  if (content.includes('Planes y Precios') && !content.includes("pricing-table")) {
    content = content.replace(/Planes y Precios/g, "'[data-testid=\"pricing-table\"]'");
    fs.writeFileSync(abs, content, 'utf-8');
    console.log('[Asclepio] Applied heuristic patch to test file');
    process.exit(0);
  }
  console.log('[Asclepio] No applicable heuristic patch found');
  process.exit(3);
} catch (err) {
  console.error('[Asclepio] Error during healing:', err?.message || err);
  process.exit(4);
}

const puppeteer = require('puppeteer');
const fs = require('fs');

(async () => {
  const browser = await puppeteer.launch({ headless: true });
  const page = await browser.newPage();
  const base = process.env.AUDIT_BASE || 'http://localhost:3002/#/dashboard';
  const dashboards = [
    '/dashboard',
    '/dashboard#/dashboard',
    '/dashboard#/dashboard?view=ceo-dashboard',
  ];

  const outDir = 'audits/screenshots';
  fs.mkdirSync(outDir, { recursive: true });

  // Navigate to base and capture
  await page.goto(base, { waitUntil: 'networkidle2', timeout: 60000 });
  await page.screenshot({ path: `${outDir}/dashboard_base.png`, fullPage: true });

  // Try opening the sidebar items programmatically
  const views = ['ceo-dashboard','cfo-dashboard','cto-dashboard','cmo-dashboard','cio-dashboard','coo-dashboard','cso-dashboard'];
  for (const view of views) {
    try {
      // Attempt to click sidebar button by label
      await page.evaluate((v) => {
        const btns = Array.from(document.querySelectorAll('button'));
        const btn = btns.find(b => b.textContent && b.textContent.toLowerCase().includes(v.split('-')[0]));
        if (btn) btn.click();
      }, view);
      await page.waitForTimeout(1200);
      const file = `${outDir}/${view}.png`;
      await page.screenshot({ path: file, fullPage: true });
      console.log('Captured', file);
    } catch (err) {
      console.error('Error capturing', view, err.message);
    }
  }

  await browser.close();
})();

#!/usr/bin/env node
// CommonJS script para capturar screenshots del dashboard en los tres planes usando puppeteer
// No usar Playwright (por requerimiento del usuario)

const fs = require('fs');
const path = require('path');
const puppeteer = require('puppeteer');

const BASE = process.env.DASHBOARD_BASE || 'http://localhost:3003';
const OUT_DIR = path.join(__dirname, '..', 'docs', 'screenshots');
const CONSOLE_LOG = process.env.DASHBOARD_CONSOLE_LOG || '/tmp/dashboard_console.log';
const REQUESTS_LOG = process.env.DASHBOARD_REQUESTS_LOG || '/tmp/dashboard_requests.log';

async function ensureOut() {
  if (!fs.existsSync(OUT_DIR)) fs.mkdirSync(OUT_DIR, { recursive: true });
}

async function capture(plan, page) {
  console.log('Capturando plan:', plan);
  await page.waitForSelector('button');
  // buscar botón por texto (normalizado) y hacer click desde el contexto de página
  const clicked = await page.evaluate(async (planText) => {
    const norm = (s) => (s || '').replace(/\s+/g,' ').trim();
    const buttons = Array.from(document.querySelectorAll('button'));
    for (const b of buttons) {
      if (norm(b.textContent) === norm(planText)) {
        b.click();
        return true;
      }
    }
    return false;
  }, plan);
  if (clicked) {
    await page.waitForTimeout(800);
  } else {
    console.warn('Botón de plan no encontrado, continuando sin cambiar plan');
  }
  const fileName = path.join(OUT_DIR, `dashboard-${plan.toLowerCase().replace(/[^a-z0-9]+/g,'')}.png`);
  await page.screenshot({ path: fileName, fullPage: true });
  console.log('Guardado:', fileName);
}

async function run() {
  await ensureOut();
  // lanzar con headless por defecto (puede ser sobreescrito con PUPPETEER_HEADLESS env)
  const headless = process.env.PUPPETEER_HEADLESS !== 'false';
  const browser = await puppeteer.launch({
    headless,
    args: [
      '--no-sandbox',
      '--disable-setuid-sandbox',
      '--disable-web-security',
      '--ignore-certificate-errors',
      '--enable-logging'
    ]
  });
  try {
    const page = await browser.newPage();
    page.setViewport({ width: 1400, height: 900 });
  const url = `${BASE}/dashboard?testMode=1`;
    console.log('Navegando a', url);

    // Prepare logs files (append mode)
    try { fs.writeFileSync(CONSOLE_LOG, `--- NEW RUN ${new Date().toISOString()} ---\n`, { flag: 'a' }); } catch(e){}
    try { fs.writeFileSync(REQUESTS_LOG, `--- NEW RUN ${new Date().toISOString()} ---\n`, { flag: 'a' }); } catch(e){}

    // Volcar console messages del navegador
    page.on('console', msg => {
      try {
        const text = `[console:${msg.type()}] ${msg.text()}\n`;
        fs.writeFileSync(CONSOLE_LOG, text, { flag: 'a' });
        console.log('PAGE_CONSOLE>', text.trim());
      } catch (e) { console.error('Error escribiendo console log', e); }
    });

    // Interceptar peticiones y respuestas
    await page.setRequestInterception(true);
    page.on('request', req => {
      try {
        let body = '';
        try {
          const pd = req.postData && typeof req.postData === 'function' ? req.postData() : req.postData || '';
          if (pd) body = ` body=${String(pd).slice(0,1000)}`;
        } catch (e) { body = '' }
        const line = `[REQ] ${req.method()} ${req.url()}${body}\n`;
        fs.writeFileSync(REQUESTS_LOG, line, { flag: 'a' });
      } catch (e) { console.error('Error escribiendo request log', e); }
      req.continue().catch(()=>{});
    });
    page.on('response', async res => {
      try {
        const status = res.status();
        const urlr = res.url();
        let text = '';
        try { text = await res.text(); } catch (e) { text = '<no-text-or-stream>'; }
        const snippet = text ? text.slice(0,1000).replace(/\n/g,' ') : '';
        const line = `[RES] ${status} ${urlr} snippet=${snippet}\n`;
        fs.writeFileSync(REQUESTS_LOG, line, { flag: 'a' });
        if (status === 0) {
          // possible network error
          fs.writeFileSync(REQUESTS_LOG, `[RES-ERR] status0 ${urlr}\n`, { flag: 'a' });
        }
      } catch (e) { console.error('Error escribiendo response log', e); }
    });
    page.on('requestfailed', req => {
      try {
        const failure = req.failure() && req.failure().errorText ? req.failure().errorText : 'unknown';
        const line = `[REQ-FAILED] ${req.method()} ${req.url()} reason=${failure}\n`;
        fs.writeFileSync(REQUESTS_LOG, line, { flag: 'a' });
      } catch (e) { }
    });

    // set a common desktop user agent to avoid some headless blocks
    try { await page.setUserAgent('Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36'); } catch(e){}

    // Inyectar token si se proporciona
    const token = process.env.DASHBOARD_AUTH_TOKEN || null;
    if (token) {
      await page.evaluateOnNewDocument((t) => {
        try { localStorage.setItem('auth_token', t); } catch(e) {}
      }, token);
      console.log('Token inyectado en localStorage (en evaluateOnNewDocument).');
    } else {
      console.log('No se proporcionó token de auth (DASHBOARD_AUTH_TOKEN).');
    }

    await page.goto(url, { waitUntil: 'networkidle0', timeout: 60000 });
    // small sleep helper
    const sleep = (ms) => new Promise((r) => setTimeout(r, ms));
    // Esperar a que el header del dashboard esté presente (más robusto que buscar botones genéricos)
    try {
      await page.waitForSelector('h2', { timeout: 30000 });
