  expect(res2).toBe('a')
  expect(k.resourceStats.tokenUsage).toBe(0)

  // Next execution should throw (t3)
  await expect(k.executeNextTask()).rejects.toThrow('fail')
  expect(k.resourceStats.tokenUsage).toBe(0)
