import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { ComposableMap, Geographies, Geography, Marker } from 'react-simple-maps';

const geoUrl = "https://cdn.jsdelivr.net/npm/world-atlas@2/countries-110m.json";

interface SeismicMapWidgetProps {
  seismicData?: any[];
}

/**
 * Normalize various external seismic payload shapes into an array of features.
 * Exported as a helper for testing.
 */
export const normalizeSeismicRaw = (raw: any): any[] => {
  if (!raw) return [];
  if (Array.isArray(raw)) return raw;
  if (raw.events && Array.isArray(raw.events)) return raw.events;
  if (raw.features && Array.isArray(raw.features)) return raw.features;
  return [];
};

const SeismicMapWidget: React.FC<SeismicMapWidgetProps> = ({ seismicData: propSeismicData }) => {
  const [magnitudeFilter, setMagnitudeFilter] = useState<number>(4.0);
  const [hoveredEvent, setHoveredEvent] = useState<any>(null);

  // Use prop data if available
  const seismicDataRaw = propSeismicData;

  const seismicArray: any[] = React.useMemo(() => normalizeSeismicRaw(seismicDataRaw), [seismicDataRaw]);

  // Filter events by magnitude and focus on LATAM region
  // Normalize each event to a consistent geojson-like shape: { id, properties: { mag, place, time }, geometry: { coordinates: [lon, lat, depth] } }
  const normalizedEvents = seismicArray.map((e: any, idx: number) => {
    // If it already looks like geojson feature, return as-is
    if (e && e.properties && e.geometry && Array.isArray(e.geometry.coordinates)) {
      return e;
    }

    // Common alternative shapes: { coordinates: [...], magnitude / mag, place, time }
    const coords = e.coordinates || e.geometry?.coordinates || e.geom || e.coords || [0, 0, 0];
    const mag = e.properties?.mag ?? e.magnitude ?? e.mag ?? 0;
    const place = e.properties?.place ?? e.place ?? e.description ?? 'Desconocido';
    const time = e.properties?.time ?? e.time ?? Date.now();

    return {
      id: e.id ?? `evt-${idx}-${time}`,
      properties: {
        mag,
        place,
        time,
        tsunami: e.properties?.tsunami ?? e.tsunami ?? 0,
        sig: e.properties?.sig ?? e.sig ?? 0,
        url: e.properties?.url ?? e.url ?? null
      },
      geometry: {
        coordinates: coords
      }
    };
  });

  const filteredEvents = normalizedEvents.filter((event: any) => {
    const mag = event.properties?.mag || 0;
    const [lon, lat] = event.geometry?.coordinates || [0, 0];

    // Focus on LATAM: roughly -120 to -30 longitude, -60 to 30 latitude
    const inLatam = lon >= -120 && lon <= -30 && lat >= -60 && lat <= 30;

    return mag >= magnitudeFilter && inLatam;
  }) || [];

  const getMarkerColor = (magnitude: number) => {
    if (magnitude >= 7.0) return '#EF4444'; // Red for major
    if (magnitude >= 6.0) return '#F59E0B'; // Orange for strong
    if (magnitude >= 5.0) return '#FCD34D'; // Yellow for moderate
    return '#10B981'; // Green for light
  };

  const getMarkerSize = (magnitude: number) => {
    return Math.max(magnitude * 2, 3);
  };

  // If no seismicData is provided, show informative state
  if (!seismicDataRaw || !seismicArray.length) {
    return (
      <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
        <CardHeader>
          <CardTitle className="text-white">Monitoreo Sísmico LATAM</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <p className="text-gray-300">Datos sísmicos no disponibles (orquestador)</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-gray-800/50 backdrop-blur-sm border-gray-700">
      <CardHeader>
        <div className="flex justify-between items-center">
          <div>
            <CardTitle className="text-white">Monitoreo Sísmico LATAM</CardTitle>
            <p className="text-gray-400 text-sm">Eventos sísmicos en tiempo real</p>
          </div>
          {/* Show mock indicator when data source is mocked */}
          <div>
            {((seismicDataRaw && seismicDataRaw.isMock) || (seismicDataRaw && seismicDataRaw.__meta && seismicDataRaw.__meta.isMock) ||
              (seismicDataRaw && seismicDataRaw.source && seismicDataRaw.source === 'builtin-mock')) && (
              <span className="ml-2 inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-600 text-black">SIMULADO</span>
            )}
          </div>
          <Select value={magnitudeFilter.toString()} onValueChange={(value) => setMagnitudeFilter(parseFloat(value))}>
            <SelectTrigger className="w-32 bg-gray-700 border-gray-600 text-white">
              <SelectValue />
            </SelectTrigger>
            <SelectContent className="bg-gray-700 border-gray-600">
              <SelectItem value="3.0">M ≥ 3.0</SelectItem>
              <SelectItem value="4.0">M ≥ 4.0</SelectItem>
              <SelectItem value="5.0">M ≥ 5.0</SelectItem>
              <SelectItem value="6.0">M ≥ 6.0</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </CardHeader>
      <CardContent>
        <div style={{ width: "100%", height: "300px" }} data-testid="seismic-map">
          <ComposableMap
            projection="geoMercator"
            projectionConfig={{
              scale: 300,
              center: [-60, -15]
            }}
            className="w-full h-full"
          >
            <Geographies geography={geoUrl}>
              {({ geographies }) =>
                geographies.map(geo => (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    fill="#374151"
                    stroke="#4B5563"
                    strokeWidth={0.5}
                    style={{
                      default: { outline: 'none' },
                      hover: { outline: 'none' },
                      pressed: { outline: 'none' },
                    }}
                  />
                ))
              }
            </Geographies>
            {filteredEvents.map((event: any) => {
              const [lon, lat] = event.geometry.coordinates;
              const mag = event.properties.mag;
              return (
                <Marker
                  key={event.id}
                  coordinates={[lon, lat]}
                  onMouseEnter={() => setHoveredEvent(event)}
                  onMouseLeave={() => setHoveredEvent(null)}
                >
                  <circle
                    r={getMarkerSize(mag)}
                    fill={getMarkerColor(mag)}
                    stroke="#FFF"
                    strokeWidth={1}
                    style={{ cursor: 'pointer' }}
                  />
                </Marker>
              );
            })}
          </ComposableMap>
        </div>

        {hoveredEvent && (
          <div className="mt-4 p-3 bg-gray-700/50 rounded-lg">
            <h4 className="text-white font-semibold">
              M{hoveredEvent.properties.mag.toFixed(1)} - {hoveredEvent.properties.place}
            </h4>
            <p className="text-gray-300 text-sm">
              {new Date(hoveredEvent.properties.time).toLocaleString()}
            </p>
            <p className="text-gray-400 text-xs">
              Profundidad: {hoveredEvent.geometry.coordinates[2]?.toFixed(1)} km
            </p>
          </div>
        )}

        <div className="mt-4">
          <h4 className="font-bold text-white mb-2">Eventos Recientes (M ≥ {magnitudeFilter}):</h4>
          <div className="space-y-1 max-h-32 overflow-y-auto">
            {filteredEvents.slice(0, 10).map((event: any) => (
              <div key={event.id} className="text-sm text-gray-300 flex justify-between">
                <span>M{event.properties.mag.toFixed(1)} - {event.properties.place.split(',')[0]}</span>
                <span className="text-gray-500">
                  {new Date(event.properties.time).toLocaleDateString()}
                </span>
              </div>
            ))}
            {filteredEvents.length === 0 && (
              <p className="text-gray-500 text-sm">No hay eventos sísmicos recientes que cumplan los criterios.</p>
            )}
          </div>
        </div>

        <div className="mt-4 flex justify-between text-xs text-gray-400">
          <span>🟢 Ligero (3.0-4.9)</span>
          <span>🟡 Moderado (5.0-5.9)</span>
          <span>🟠 Fuerte (6.0-6.9)</span>
          <span>🔴 Mayor (7.0+)</span>
        </div>
      </CardContent>
    </Card>
  );
};

export default SeismicMapWidget;

