import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

type Action =
  | {
      type: (typeof actionTypes)["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: (typeof actionTypes)["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: (typeof actionTypes)["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: (typeof actionTypes)["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

// listeners are callbacks subscribed by components; the parameter is used by callers.

/* eslint-disable no-unused-vars */
const listeners: Array<(state: State) => void> = []
/* eslint-enable no-unused-vars */


let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [_state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [])

  return {
    ..._state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

import { useEffect } from 'react';
import { useQueryClient } from '@tanstack/react-query';

// Helper to fetch JSON and throw a clear error on network/HTTP failures
async function fetchJson(url: string) {
  const res = await fetch(url);
  if (!res.ok) {
    // Try to parse JSON body for richer diagnostics
    let body: any = null;
    try {
      body = await res.json();
    } catch (e) {
      try {
        body = await res.text();
      } catch (e2) {
        body = null;
      }
    }
    const bodyStr = body ? (typeof body === 'string' ? body : JSON.stringify(body)) : '';
    throw new Error(`Fetch ${url} failed: ${res.status} ${res.statusText} ${bodyStr}`);
  }
  return res.json();
}

export const usePrefetch = () => {
  let queryClient;
  try {
    queryClient = useQueryClient();
  } catch (_e) {
    // If no QueryClientProvider is present (e.g. some tests), provide
    // a minimal fallback that implements the methods we call so code
    // can run without throwing. This keeps tests isolated and avoids
    // having to wrap every render with a provider.
    queryClient = {
      prefetchQuery: async () => Promise.resolve(),
    } as any;
  }

  const prefetchSDLCData = async () => {
    try {
      // Prefetch critical SDLC data
      await Promise.all([
        queryClient.prefetchQuery({
          queryKey: ['sdlc', 'full-state'],
          queryFn: () => fetchJson('/api/sdlc/full-state'),
          staleTime: 5 * 60 * 1000, // 5 minutes
          retry: 0,
        }),
        queryClient.prefetchQuery({
          queryKey: ['kanban', 'board'],
          queryFn: () => fetchJson('/api/kanban/board'),
          staleTime: 5 * 60 * 1000,
          retry: 0,
        }),
      ]);
    } catch (error) {
      console.warn('[Prefetch] Failed to prefetch critical data:', error);
    }
  };

  const prefetchRoute = (route: string) => {
    // Prefetch route-specific data
    switch (route) {
      case '/sdlc-dashboard':
        prefetchSDLCData();
        break;
      case '/dashboard':
        queryClient.prefetchQuery({
          queryKey: ['dashboard', 'global-risk'],
          queryFn: () => fetchJson('/api/global-risk/summary'),
          staleTime: 10 * 60 * 1000, // 10 minutes
          retry: 0,
        });
        break;
      case '/demo':
        queryClient.prefetchQuery({
          queryKey: ['demo', 'data'],
          queryFn: () => fetchJson('/api/demo/predictive-data'),
          staleTime: 15 * 60 * 1000, // 15 minutes
          retry: 0,
        });
        break;
      default:
        break;
    }
  };

  const prefetchOnHover = (route: string) => {
    // Debounce prefetching to avoid excessive requests
    let timeoutId: NodeJS.Timeout;

    return () => {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => prefetchRoute(route), 100);
    };
  };

  const prefetchOnVisible = (route: string) => {
    // Use Intersection Observer for prefetching when route becomes visible
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            prefetchRoute(route);
            observer.disconnect();
          }
        });
      },
      { threshold: 0.1 }
    );

    return observer;
  };

  return {
    prefetchSDLCData,
    prefetchRoute,
    prefetchOnHover,
    prefetchOnVisible,
  };
};

